(23,23,7,'Key Squares','[{\"number\":1,\"type\":\"html\",\"content\":\"<p class=\\\"mb-2 mt-4 font-bold\\\">Key Squares for Pawns on the seventh rank<\\/p>\"},{\"number\":2,\"type\":\"html\",\"content\":\"<p class=\\\"mb-2\\\">When the pawn is on the seventh rank, there are five key squares, the squares on the seventh and eighth rank next to that pawn.<\\/p>\"},{\"number\":3,\"type\":\"html\",\"content\":\"<p class=\\\"mb-2\\\">Look at the example below. The key squares for the d7 pawn are c7, c8, d8, e7, and e8.<\\/p>\"},{\"number\":4,\"type\":\"chess-image\",\"content\":{\"position\":\"8\\/3P4\\/8\\/8\\/8\\/8\\/8\\/8 w - - 0 1\",\"chessState\":{\"moveHistory\":[{\"fen\":\"8\\/3P4\\/8\\/8\\/8\\/8\\/8\\/8 w - - 0 1\",\"sound\":\"move\",\"lastMoves\":[],\"checkSquare\":null,\"arrows\":[],\"highlightedSquares\":[{\"square\":3,\"color\":\"#EFB701\"},{\"square\":4,\"color\":\"#EFB701\"},{\"square\":5,\"color\":\"#EFB701\"},{\"square\":11,\"color\":\"#EFB701\"},{\"square\":13,\"color\":\"#EFB701\"}],\"toMove\":\"player\",\"newMove\":false}],\"movePointer\":0},\"exclude\":[\"right-click\",\"left-click\",\"engine\",\"chessjs\"]}}]','2022-05-08 00:00:00','2023-01-30 16:19:17'),
