(63,45,1,'Queen vs. Pawn','[{\"number\":1,\"type\":\"html\",\"content\":\"<p class=\\\"mb-2\\\">In this lesson, you\'ll learn to play endgames with a queen against a pawn that\'s about to queen.<\\/p>\"},{\"number\":2,\"type\":\"html\",\"content\":\"<p class=\\\"mb-2\\\">We\'ll look at two different cases:<\\/p>\"},{\"number\":3,\"type\":\"html\",\"content\":\"<ol start=\\\"1\\\"><li>Queen vs. knight or center pawn.<\\/li><\\/ol>\"},{\"number\":4,\"type\":\"html\",\"content\":\"<ol start=\\\"2\\\"><li>Queen vs. rook or bishop\'s pawn.<\\/li><\\/ol>\"}]','2022-07-13 00:00:00','2023-01-30 16:19:18'),
