(128,85,6,'Two Connected Pawns (With Rook Pawn)','[{\"number\":1,\"type\":\"info\",\"content\":\"<p class=\\\"mb-2\\\">When both connected pawns are two squares away from promoting and one of the pawns is a rook pawn, there are some drawing possibilities.<\\/p>\"},{\"number\":2,\"type\":\"html\",\"content\":\"<p class=\\\"mb-2\\\">In the following example, white is unable to improve the position.<\\/p>\"},{\"number\":3,\"type\":\"html\",\"content\":\"<ul><li>If white plays a7, black will respond with Kb7.<\\/li><\\/ul>\"},{\"number\":4,\"type\":\"html\",\"content\":\"<ul class=\\\"mb-2\\\"><li>If white plays b7, black will sacrifice the bishop for both pawns.<\\/li><\\/ul>\"},{\"number\":5,\"type\":\"chess-lesson\",\"content\":{\"position\":\"2k5\\/8\\/PP6\\/2K5\\/5B2\\/8\\/6b1\\/8 w - - 0 1\",\"rotate\":false,\"chessState\":{\"moveHistory\":[{\"fen\":\"2k5\\/8\\/PP6\\/2K5\\/5B2\\/8\\/6b1\\/8 w - - 0 1\",\"sound\":\"move\",\"lastMoves\":[],\"checkSquare\":null,\"arrows\":[],\"highlightedSquares\":[],\"toMove\":\"player\",\"newMove\":false},{\"fen\":\"2k5\\/P7\\/1P6\\/2K5\\/5B2\\/8\\/6b1\\/8 b - - 0 1\",\"sound\":\"move\",\"lastMoves\":[17,9],\"checkSquare\":null,\"arrows\":[{\"id\":\"2f2bf3f0-1639-11ed-8b9a-af0d7edda385\",\"firstSquare\":17,\"secondSquare\":9,\"color\":\"#EFB701\"}],\"highlightedSquares\":[],\"toMove\":\"engine\",\"newMove\":false},{\"fen\":\"8\\/Pk6\\/1P6\\/2K5\\/5B2\\/8\\/6b1\\/8 w - - 1 2\",\"sound\":\"move\",\"lastMoves\":[3,10],\"checkSquare\":null,\"arrows\":[{\"id\":\"303c2b70-1639-11ed-8b9a-af0d7edda385\",\"firstSquare\":3,\"secondSquare\":10,\"color\":\"#EFB701\"}],\"highlightedSquares\":[],\"toMove\":\"player\",\"newMove\":false}],\"movePointer\":0},\"exclude\":[\"left-click\",\"right-click\",\"engine\"]}}]','2022-08-07 00:00:00','2023-01-30 16:19:20'),
